import React from 'react';

function Summary({ cart, total }) {
  return (
    <div className="col-md-4 summary p-4">
      <h5><b>Summary</b></h5>
      <hr />
      {Object.entries(cart).map(([name, quantity]) => (
        <div key={name} className="row">
          <h6>{name} <span className="badge bg-dark">{quantity}개</span></h6>
        </div>
      ))}
      <div className="row pt-2 pb-2 border-top">
        <h5 className="col">총금액</h5>
        <h5 className="col text-end">{total}원</h5>
      </div>
      <button className="btn btn-dark col-12">결제하기</button>
    </div>
  );
}

export default Summary;
