import React, { useState } from 'react';
import ProductList from './ProductList';
import Summary from './Summary';
import CheckoutForm from './CheckoutForm';
import './App.css';  // ÎòêÎäî index.css


const initialProducts = [
  { id: 1, name: 'Columbia Nari√±√≥', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
  { id: 2, name: 'Brazil Serra Do Capara√≥', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
  { id: 3, name: 'Columbia Nari√±√≥', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
];

function App() {
  const [cart, setCart] = useState({});

  const addToCart = (product) => {
    setCart((prevCart) => ({
      ...prevCart,
      [product.name]: (prevCart[product.name] || 0) + 1,
    }));
  };

  const total = Object.keys(cart).reduce((acc, name) => {
    const price = initialProducts.find((p) => p.name === name)?.price || 0;
    return acc + price * cart[name];
  }, 0);

  return (
    <div className="container-fluid">
      <div className="row justify-content-center m-4">
        <h1 className="text-center">Grids & Circle</h1>
      </div>

      {/* üëá Ïó¨Í∏∞Î∂ÄÌÑ∞ Í≥†Ïπú Î∂ÄÎ∂Ñ! */}
      <div className="card" style={{ maxWidth: '950px', margin: 'auto' }}>
        <div className="row align-items-stretch">
          {/* ÏôºÏ™Ω: ÏÉÅÌíà Î™©Î°ù */}
          <div className="col-md-8 mt-4 d-flex flex-column align-items-start p-3 pt-0">
            <ProductList products={initialProducts} onAdd={addToCart} />
          </div>

          {/* Ïò§Î•∏Ï™Ω: Summary + Ìèº */}
          <div className="col-md-4 summary p-4">
            <Summary cart={cart} total={total} />
            <CheckoutForm />
          </div>
        </div>
      </div>
    </div>
  );
}


export default App;
