import React, { useState } from 'react';
import ProductItem from './ProductItem';
import Summary from './Summary';
import CheckoutForm from './CheckoutForm';

const initialProducts = [
  { id: 1, name: 'Columbia Nariñó', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
  { id: 2, name: 'Brazil Serra Do Caparaó', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
  { id: 3, name: 'Columbia Nariñó', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
];

function App() {
  const [cart, setCart] = useState({});

  const addToCart = (product) => {
    setCart((prevCart) => ({
      ...prevCart,
      [product.name]: (prevCart[product.name] || 0) + 1,
    }));
  };

  const total = Object.keys(cart).reduce((acc, name) => {
    const price = initialProducts.find((p) => p.name === name)?.price || 0;
    return acc + price * cart[name];
  }, 0);

  return (
    <div style={{ backgroundColor: '#ddd', minHeight: '100vh', padding: '2rem' }}>
      <div className="container">
        <div className="row justify-content-center mb-4">
          <h1 className="text-center">Grids & Circle</h1>
        </div>

        <div className="card">
          <div className="row g-0" style={{ height: '100%' }}>
            {/* 왼쪽 상품 목록 */}
            <div className="col-md-8 d-flex flex-column justify-content-between">
              <div className="p-4 h-100 d-flex flex-column">
                <h5><b>상품 목록</b></h5>
                <div className="flex-grow-1 d-flex flex-column gap-3 justify-content-start">
                  {initialProducts.map(product => (
                    <div
                      key={product.id}
                      style={{
                        background: '#fff',
                        padding: '1rem 1rem',
                        borderRadius: '12px',
                        border: '1px solid #ddd',
                        minHeight: '100px',
                        display: 'flex',
                        alignItems: 'center',
                        width: '100%',
                        boxSizing: 'border-box'
                      }}
                    >
                      <ProductItem product={product} onAdd={addToCart} />
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* 오른쪽 Summary */}
            <div className="col-md-4 summary p-4">
              <Summary cart={cart} total={total} />
              <CheckoutForm />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
