// ✅ OrderPage.js - 주문 및 결제 페이지
import React, { useState } from 'react';

const initialProducts = [
  { id: 1, name: 'Columbia Nariñó', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
  { id: 2, name: 'Brazil Serra Do Caparaó', price: 5000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
  { id: 3, name: 'Kenya AA', price: 6000, image: 'https://i.imgur.com/HKOFQYa.jpeg' },
];

function OrderPage() {
  const [cart, setCart] = useState({});
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [postcode, setPostcode] = useState('');

  const addToCart = (product) => {
    setCart((prevCart) => ({
      ...prevCart,
      [product.id]: (prevCart[product.id] || 0) + 1,
    }));
  };

  const handleCheckout = () => {
    const orderProducts = Object.keys(cart).map((productId) => ({
      productId: parseInt(productId),
      productStock: cart[productId],
    }));

    const orderData = {
      email,
      address,
      postcode,
      orderProducts,
    };

    fetch('http://localhost:8080/api/order', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(orderData),
    })
      .then((res) => res.json())
      .then(() => alert('결제 성공!'))
      .catch((err) => {
        console.error(err);
        alert('결제 실패!');
      });
  };

  return (
    <div style={{ backgroundColor: '#ddd', minHeight: '100vh', padding: '2rem' }}>
      <div className="container">
        <h1 className="text-center mb-4">주문 페이지</h1>

        <div className="row mb-4">
          {initialProducts.map(product => (
            <div key={product.id} className="col-md-4">
              <div className="card h-100">
                <img src={product.image} alt={product.name} className="card-img-top" style={{ height: '200px', objectFit: 'contain' }} />
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">가격: {product.price.toLocaleString()}원</p>
                  <button className="btn btn-outline-dark w-100" onClick={() => addToCart(product)}>추가</button>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="card p-4">
          <h5 className="mb-3">배송 정보</h5>
          <input type="email" className="form-control mb-2" placeholder="이메일" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="text" className="form-control mb-2" placeholder="주소" value={address} onChange={(e) => setAddress(e.target.value)} />
          <input type="text" className="form-control mb-3" placeholder="우편번호" value={postcode} onChange={(e) => setPostcode(e.target.value)} />
          <button className="btn btn-dark w-100" onClick={handleCheckout}>결제하기</button>
        </div>
      </div>
    </div>
  );
}

export default OrderPage;
