import React from 'react';

function ProductItem({ product, onAdd }) {
    return (
      <li className="list-group-item d-flex mt-2 align-items-center">
        <div className="col-2">
        <img
  src={product.image}
  alt={product.name}
  style={{
    width: '60px',
    height: '60px',
    objectFit: 'cover',
    borderRadius: '8px',
    marginRight: '15px' // ✅ 이게 핵심!
  }}
/>

        </div>
        <div className="col">
  <div className="text-muted">커피콩</div>
  <div className="fw-bold">{product.name}</div>
</div>
        <div className="col text-center price">{product.price}원</div>
        <div className="col text-end action">
          <button className="btn btn-sm btn-outline-dark" onClick={() => onAdd(product)}>추가</button>
        </div>
      </li>
    );
  }


export default ProductItem;
